<?phprequire 'required-include.php';// disable time limit; if not, under load, image processing may failset_time_limit(0);// create configuration$configuration = new Settings(get_vm_dir() . '/server/configuration.json');// create service$service = new Service($configuration);// convert application/xml to text/html; some browsers--especially mobile browsers--default to XMLif (WebHttp::getAccept() == 'application/xml') {    $service->accept = 'text/html';}// execute$service->execute();